function kiemTraEmail(e){let n=$(e)[0].value,t=document.querySelector("#customer-email").parentElement.parentElement.querySelector(".msg-err");return""===n?(t.innerHTML="Vui lòng nhập email",!1):/^[a-z][a-z0-9_\.]{5,32}@[a-z0-9]{2,}(\.[a-z0-9]{2,4}){1,2}$/.test(n)?(t.innerHTML="",!0):(t.innerHTML="Không phải là email",!1)}$(".header__navbar__item--home").addClass("active"),setTimeout((()=>{$(".app").removeClass("hidden"),$(".loading").addClass("hidden")}),2e3),$(".btn__send__email").click((()=>{let e=$("#customer-email")[0].value;kiemTraEmail($("#customer-email")[0])&&Email.sendEmail(e.trim(),"THHHT Shop Xin Chào Bạn!","Bạn đã kích hoạt thành công nhận thông tin sản phẩm mới nhất từ shop").then((()=>toast({title:"Thành công!",message:"Gửi thành công.",type:"success",duration:5e3})))})),$("#customer-email").blur((function(){kiemTraEmail(this)}));