function Sitemap(){const i=document.querySelector(".sitemap");if(i){const t=i.getAttribute("data-sitemap-item").split(", "),n=i.getAttribute("data-sitemap-link").split(", "),e=i.getAttribute("data-sitemap-title");let a=`\n            <div class="container sitemap__container">\n                <h1 class="sitemap__title hidden-sm ${e??"hidden"}">\n                    ${e}\n                </h1>\n                <div class="sitemap__list">\n                    ${t.map(((i,t)=>`\n                            <div class="sitemap__item">\n                                <a href="${window.location.href.includes("WebSite_BanGiay_T3H2")?"/WebSite_BanGiay_T3H2":""}${"?"===n[t]?"":n[t]}" class="sitemap__link">\n                                    ${i}\n                                </a>\n                            </div>\n                        `)).join('\n                            <div class="sitemap__icon">\n                                <i class="fas fa-angle-right"></i>\n                            </div>\n                        ')}\n                    \n                </div>\n            </div>\n        `;i.innerHTML=a}}